var measurePlan = []


/**
* @param {array} plan
*/
function getNext(plan){

    while (plan.length >0){

        if (plan[0].active){
            return plan;
        }
        else{
            plan.shift();
        }

    }   

    return false;

}


if (msg.payload == "Start"){

    let config = global.get("config");
    measurePlan = [
        { "active": config.m1_active, "name": "m1" },
        { "active": config.m2_active, "name": "m2" },
        { "active": config.m3_active, "name": "m3" },
        { "active": config.m4_active, "name": "m4" },
        { "active": config.m5_active, "name": "m5" },
        { "active": config.m5_active, "name": "m5_1" },
        { "active": config.m5_active, "name": "m6" },
        { "active": config.m7_active, "name": "m7" },
        { "active": config.m8_active, "name": "m8" },
        { "active": config.m9_active, "name": "m9" },
        { "active": config.m10_active, "name": "m10" }
    ]



    var next = getNext(measurePlan)
    if (next) {
        flow.set("measurePlan", next);

        msg.payload = next[0].name;
        return msg;
    }
    else {
        node.warn("Please enable measurements in Settings Tab");
        msg.payload="Error"
        return msg;
    }

}


else{

    measurePlan = flow.get("measurePlan") || [];

    if (measurePlan.length > 0){
        //match for payload in current state
        if (msg.payload == measurePlan[0].name){

            //Remove current state
            measurePlan.shift()

            var next = getNext(measurePlan)
            if (next) {
                flow.set("measurePlan", next);

                msg.payload = next[0].name;
                return msg;

            }
            else {
                msg.payload = "Start";
                return msg;
            }
        }
    }
}

return null;

