var measurePlan = []


/**
* @param {array} plan
*/
function getNext(plan){

    while (plan.length >0){

        if (plan[0].order){
            return plan;
        }
        else{
            plan.shift();
        }

    }   

    return false;

}



if (msg.payload == "Start"){

    let config = global.get("config");
    let types = global.get("measurement_types")


    let measurePlan = [];


    config.m1_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m1"].long_name, "sensor": config.m1_sensor_id })
    });
    config.m2_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m2"].long_name, "sensor": config.m2_sensor_id })
    });
    config.m3_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m3"].long_name, "sensor": config.m3_sensor_id })
    });
    config.m4_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m4"].long_name, "sensor": config.m4_sensor_id })
    });
    config.m5_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m5"].long_name, "sensor": config.m5_sensor_id })
        measurePlan.push({ "order": element, "name": types["m5_1"].long_name, "sensor": config.m5_1_sensor_id })
    });
    config.m6_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m6"].long_name, "sensor": config.m6_sensor_id })
    });
    config.m7_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m7"].long_name, "sensor": config.m7_sensor_id })
        measurePlan.push({ "order": element, "name": types["m7_1"].long_name, "sensor": config.m7_sensor_id })
    });
    config.m8_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m8"].long_name, "sensor": config.m8_sensor_id })
        measurePlan.push({ "order": element, "name": types["m8_1"].long_name, "sensor": config.m8_sensor_id })
    });
    config.m9_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m9"].long_name, "sensor": config.m9_sensor_id })
    });
    config.m10_order.forEach(function (element) {
        measurePlan.push({ "order": element, "name": types["m10"].long_name, "sensor": config.m10_sensor_id })
    });


    //Filter operations that have no sequence number
    let filteredPlan = measurePlan.filter((operation) => {
        return operation.order != null;
    });

    //Sort operations according to sequence number
    filteredPlan.sort(function (a, b) {
            return a.order - b.order;
    });

    var next = getNext(filteredPlan)
    if (next) {
        flow.set("measurePlan", next);

        msg.payload = next[0].name;
        return msg;
    }
    else {
        node.warn("Please enable measurements in Settings Tab");
        msg.payload="Error"
        return msg;
    }

}

else if (msg.payload == "Return"){
    var plan = flow.get("measurePlan");
    var element = flow.get("lastMeasurement")
    if (element){
        plan.unshift(element)
        flow.set("measurePlan", plan);
    }
}


else{

    measurePlan = flow.get("measurePlan") || [];

    if (measurePlan.length > 0){
        //match for payload in current state
        if (msg.payload == measurePlan[0].name){

            //Remove current state
            var element = measurePlan.shift()
            flow.set("lastMeasurement", element)

            var next = getNext(measurePlan)
            if (next) {
                flow.set("measurePlan", next);

                msg.payload = next[0].name;
                return msg;

            }
            else {
                msg.payload = "Start";
                return msg;
            }
        }
    }
}

return null;

