// Build CSV text manually from Postgres rows in msg.payload
// - Delimiter: ;
// - Header row included
// - BOM + "sep=;" prefix for Excel
// - Timestamp in Europe/Berlin: DD.MM.YYYY HH:MM:SS
// - Decimal comma for numeric fields
// - Proper CSV quoting (wrap & escape " by doubling)

function fmtBerlin(dt){
  const parts = new Intl.DateTimeFormat('de-DE', {
    timeZone: 'Europe/Berlin',
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hour12: false
  }).formatToParts(dt).reduce((o,p)=> (o[p.type]=p.value,o),{});
  return `${parts.day}.${parts.month}.${parts.year} ${parts.hour}:${parts.minute}:${parts.second}`;
}
function decComma(v){
  if (v === null || v === undefined || v === '') return '';
  // stringify numbers safely (keeps dot), then swap first decimal dot to comma
  const s = (typeof v === 'number') ? String(v) : String(v);
  // Only replace the first dot (decimal); if you need full locale formatting, use Intl.NumberFormat.
  const i = s.indexOf('.');
  return i >= 0 ? s.slice(0,i) + ',' + s.slice(i+1) : s;
}
function csvEscape(val){
  // Turn value into string and escape for CSV
  const s = (val === null || val === undefined) ? '' : String(val);
  // If contains ; " \n \r, wrap in quotes and double quotes inside
  if (/[;"\r\n]/.test(s)) return '"' + s.replace(/"/g,'""') + '"';
  return s;
}

const rows = Array.isArray(msg.payload) ? msg.payload : (msg.payload ? [msg.payload] : []);
// Define output column order and how to compute each cell
const columns = [
  { key: 'Erstellt am',      get: r => fmtBerlin(new Date(r.created)) },
  { key: 'Station',          get: r => r.station ?? '' },
  { key: 'Los',              get: r => r.lot ?? '' },
  { key: 'RingID',           get: r => r.ringid ?? '' },
  { key: 'Merkmal',          get: r => r.type ?? '' },
  { key: 'Wert',             get: r => decComma(r.value) },
  { key: 'Obergrenze',       get: r => decComma(r.uppert) },
  { key: 'Untergrenze',      get: r => decComma(r.lowert) },
  { key: 'Nennmaß',          get: r => decComma(r.nennmass) },
  { key: 'Abmaß',            get: r => decComma(r.abmass) },
  { key: 'OK',               get: r => r.pass ?? '' },
  { key: 'Bedienfehler',     get: r => r.usererror ?? '' },
  { key: 'Daten',            get: r => Array.isArray(r.data) ? JSON.stringify(r.data) : (r.data ?? '') }
];

const header = columns.map(c => c.key).join(';');
const body = rows.map(r => columns.map(c => csvEscape(c.get(r))).join(';')).join('\n');

// UTF-8 BOM + Excel delimiter hint + header + lines
const bom = '\uFEFF';
msg.payload = bom + header + (body ? '\n' + body : '');

const start = (msg.req && msg.req.query && msg.req.query.start) ? msg.req.query.start.slice(0,10) : new Date().toISOString().slice(0,10);
msg.headers = {
  'Content-Type': 'text/csv; charset=utf-8',
  'Content-Disposition': `attachment; filename="messungen_${start}.csv"`
};
return msg;

