	SET  usererror=True
	WHERE created= TO_TIMESTAMP('{{msg.lastInsert}}') AND station = '{{{msg.station}}}' AND type = '{{{msg.type}}}';

func:
let length = msg.length
msg.payload = [{
    "series": ["Minimum", "Maximum", "Durchschnitt", "Messwerte"],
