let measureResult = msg.span;

let config = global.get("config");

let upper = config.m6_abmass + config.m6_ot; 
let lower = config.m6_abmass + config.m6_ut; 

if (measureResult >= lower && measureResult <= upper)
{
    msg.pass = true
}
else
{
    msg.pass = false
}
msg.value = measureResult;

return msg;

